import { NavDropdown } from 'react-bootstrap';

import { Link } from 'react-router-dom';
import { useGlobalContext } from '../context';

const Navbar = () => {
  const styles = {
    textDecoration: 'none',
  };

  const { userInfo, setUserInfo } = useGlobalContext();
  //const { userInfo, signout } = useGlobalContext();
  const signoutHandler = () => {
    setUserInfo(null);
    // signout();
    localStorage.removeItem('userInfo');
  };
  return (
    <div className="div-center">
      <ul className="nav-list">
        <li>
          {userInfo ? (
            <NavDropdown title="Your Data">
              <ul className="dropdown-container">
                <li>
                  <Link to="/users/joblist" style={styles}>
                    {' '}
                    Job List{' '}
                  </Link>
                </li>
                <li>
                  {' '}
                  <Link to="/users/postjob" style={styles}>
                    Post Job
                  </Link>
                </li>
              </ul>
            </NavDropdown>
          ) : (
            <Link to="/users/login" style={styles}>
              Login
            </Link>
          )}
        </li>
        <li>
          <Link to="/" style={styles}>
            <menuitem>Home </menuitem>
          </Link>{' '}
        </li>

        {userInfo && (
          <li>
            <Link to="/" style={styles} onClick={signoutHandler}>
              Sign Out
            </Link>
          </li>
        )}

        {userInfo ? (
          <li className="user-name">
            {' '}
            <p>Welcome to your personal job service</p>{' '}
            <span>
              <strong>{userInfo.name} </strong>{' '}
            </span>
          </li>
        ) : (
          <li>
            <Link style={styles} to="/users/signup">
              Sign Up
            </Link>{' '}
          </li>
        )}
      </ul>
    </div>
  );
};

export default Navbar;
