import axios from 'axios';
import React from 'react';
import {
  Button,
  Form,
  FormControl,
  FormGroup,
  FormLabel,
} from 'react-bootstrap';

import { toast } from 'react-toastify';

const GetAnyJobsCopy = () => {
  const [position, setPosition] = React.useState('');
  const [loading, setLoading] = React.useState(false);
  const [jobs, setJobs] = React.useState(null);

  const setPositionHandler = React.useCallback(
    (e) => setPosition(e.target.value.toLowerCase()),
    []
  );

  const submitHandler = async (e) => {
    e.preventDefault();
    // alert(position);

    setLoading(true);
    try {
      const queryObject = { function: position.toLowerCase() };
      //console.log(queryObject);
      const search_jobs = await axios.get(
        `/api/v1/jobs/getjobs/${position}`,
        queryObject
      );
      //console.log(search_jobs.data.length);
      if (search_jobs.data.length !== 0) {
        setJobs(search_jobs.data);
      } else {
        setJobs([]);
      }

      //console.log(jobs);
      setLoading(false);
    } catch (err) {
      toast.error(err.message);
      setLoading(false);
    }
  };

  const getusersjobsHandler = async (createdBy) => {
    console.log(createdBy);
    setLoading(true);
    try {
      const usersjobs = await axios.get(
        `/api/v1/jobs/getjobs/getusersjobs/${createdBy}`,
        { createdBy }
      );
      // console.log(usersjobs);
      setJobs(usersjobs.data);
      setLoading(false);
    } catch (err) {
      toast.error(err.message);
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="div-center">LOADING...</div>;
  }

  return <div>copy</div>;
};

export default GetAnyJobsCopy;
