import axios from 'axios';
import React from 'react';
//import reducer from './reducer';

const AppContext = React.createContext();

/*
const initialState = {
  userInfo: localStorage.getItem('userInfo')
    ? JSON.parse(localStorage.getItem('userInfo'))
    : null,
}; */

const AppProvider = ({ children }) => {
  const [loading, setLoading] = React.useState(false);
  // const [state, dispatch] = React.useReducer(reducer, initialState);
  const [userInfo, setUserInfo] = React.useState(
    localStorage.getItem('userInfo')
      ? JSON.parse(localStorage.getItem('userInfo'))
      : null
  );
  /*
  const login = (userInfo) => {
    dispatch({ type: 'LOGIN', payload: userInfo });
  };

  const signout = () => {
    dispatch({ type: 'SIGNOUT' });
  };
*/
  const findJobs = async (userId) => {
    setLoading(true);
    try {
      const jobs = await axios.get(`/api/v1/users/getjob/${userId}`);
      console.log(jobs);
      setLoading(false);
    } catch (err) {
      setLoading(false);
      console.log(err.message);
    }
    //alert(jobToFind.position);
  };

  /*</AppContext.Provider><AppContext.Provider
      value={{ ...state, loading, setLoading, login, signout, findJob }}
    >*/

  return (
    <AppContext.Provider value={{ userInfo, setUserInfo, loading, setLoading }}>
      {children}{' '}
    </AppContext.Provider>
  );
};

const useGlobalContext = () => {
  return React.useContext(AppContext);
};

export { AppProvider, useGlobalContext };
