import {
  Button,
  Form,
  FormControl,
  FormGroup,
  FormLabel,
} from 'react-bootstrap';
import { useGlobalContext } from '../context';
import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { useCallback } from 'react';
import { toast } from 'react-toastify';

const EditJob = () => {
  const { userInfo } = useGlobalContext();
  const navigate = useNavigate();
  const { id } = useParams();

  // console.log(id);

  const [position, setPosition] = React.useState('');
  const [company, setCompany] = React.useState('');
  const [started, setStarted] = React.useState('');
  const [finished, setFinished] = React.useState('');

  const [loading, setLoading] = React.useState(false);

  const [job, setJob] = React.useState({});

  const url = `/api/v1/jobs/getjob/job/${id}`;

  const fetchJob = useCallback(async () => {
    try {
      //console.log(url);
      //console.log('hi');
      const fetched_job = await axios.get(url);
      setJob(fetched_job.data);
      setCompany(fetched_job.data.company);
      setPosition(fetched_job.data.function);
      setStarted(fetched_job.data.started);
      setFinished(fetched_job.data.finished);

      //console.log(job);
      setLoading(false);
    } catch (err) {
      toast.error(err.message);
      setLoading(false);
    }
  }, [url]);

  React.useEffect(() => {
    setLoading(true);

    fetchJob();
  }, [fetchJob]);

  // console.log(job);

  const submitHandler = async (e) => {
    e.preventDefault();
    const patch_url = `/api/v1/jobs/editjob/${job._id}`;
    // alert(position);
    const newJob = {
      function: position.toLowerCase(),
      company: company.toLowerCase(),
      started: started,
      finished: finished,
    };
    // console.log(newJob);
    try {
      // alert('hi');
      // console.log(job);
      await axios.patch(patch_url, newJob);
      navigate('/users/joblist');
    } catch (err) {
      toast.error(err.message);
    }
  };

  if (loading) {
    return <div className="div-center"> LOADING... </div>;
  }

  return (
    <div className="div-center">
      {' '}
      {userInfo && <div> {userInfo.name} </div>} <h3> Edit Job </h3>{' '}
      <Form onSubmit={submitHandler}>
        <FormGroup>
          <FormLabel> Position </FormLabel>{' '}
          <FormControl
            type="text"
            onChange={(e) => setPosition(e.target.value)}
            value={position}
          />{' '}
        </FormGroup>{' '}
        <FormGroup>
          <FormLabel> Company </FormLabel>{' '}
          <FormControl
            type="text"
            onChange={(e) => setCompany(e.target.value)}
            value={company}
          />{' '}
        </FormGroup>{' '}
        <FormGroup>
          <FormLabel> Started </FormLabel>{' '}
          <FormControl
            type="text"
            onChange={(e) => setStarted(e.target.value)}
            value={started}
          />{' '}
        </FormGroup>{' '}
        <FormGroup>
          <FormLabel> Finished </FormLabel>{' '}
          <FormControl
            type="text"
            onChange={(e) => setFinished(e.target.value)}
            value={finished}
          />{' '}
        </FormGroup>{' '}
        <div className="btn-container">
          <Button type="submit"> Submit </Button>{' '}
        </div>{' '}
      </Form>{' '}
    </div>
  );
};

export default EditJob;
