import {
  Form,
  FormControl,
  FormLabel,
  Button,
  FormGroup,
} from 'react-bootstrap';

import React from 'react';
import { useGlobalContext } from '../context';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = React.useState('');
  const [name, setName] = React.useState('');
  const [password, setPassword] = React.useState('');

  //const { userInfo, setUserInfo, setLoading } = useGlobalContext();
  const { login, setLoading } = useGlobalContext();
  const navigate = useNavigate();

  const submitHandler = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post('/api/v1/users/login', {
        name,
        email,
        password,
      });
      const user = await axios.get(`/api/v1/users/login/${email}`);
      //alert(user.data.name);
      //console.log(user.data.name);
      // setUserInfo({ name: user.data.name, email: email, password: password });
      login({ name: user.data.name, email: email, password: password });
      window.localStorage.setItem('userInfo', JSON.stringify({ name, email }));
      navigate('/users/details');
      setLoading(false);
    } catch (err) {
      alert(err.message);
      setLoading(false);
    }
  };

  return (
    <div className="div-center">
      <Form onSubmit={submitHandler}>
        <FormGroup>
          <FormLabel> Name </FormLabel>
          <FormControl type="text" onChange={(e) => setName(e.target.value)} />
        </FormGroup>
        <FormGroup>
          <FormLabel> Email </FormLabel>
          <FormControl
            type="email"
            required
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
        </FormGroup>
        <FormGroup>
          <FormLabel> Password </FormLabel>
          <FormControl
            type="password"
            required
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          />
        </FormGroup>
        <div className="btn-container">
          <Button type="submit">Login</Button>
        </div>
      </Form>
    </div>
  );
};

export default Login;
