import axios from 'axios';
import React from 'react';
import {
  Button,
  Form,
  FormControl,
  FormGroup,
  FormLabel,
} from 'react-bootstrap';

const GetAnyJobs = () => {
  const [position, setPosition] = React.useState('');
  const [loading, setLoading] = React.useState(false);
  const [jobs, setJobs] = React.useState({});

  const submitHandler = async (e) => {
    e.preventDefault();
    // alert(position);
    setLoading(true);
    try {
      const queryObject = { function: position };
      console.log(queryObject);
      const search_jobs = await axios.get(
        `/api/v1/jobs/getjobs/${position}`,
        queryObject
      );
      alert(search_jobs.data);

      setJobs(search_jobs.data);
      console.log(jobs);
      setLoading(false);
    } catch (err) {
      console.log(err.message);
      setLoading(false);
    }
  };
  if (loading) {
    return <div className="div-center">LOADING...</div>;
  }
  return (
    <div className="div-center">
      <Form onSubmit={submitHandler}>
        <FormGroup>
          <FormLabel>Position</FormLabel>
          <FormControl
            type="text"
            onChange={(e) => setPosition(e.target.value)}
          />
        </FormGroup>
        <div className="btn-container">
          <Button type="submit">Search</Button>
        </div>
      </Form>
    </div>
  );
};

export default GetAnyJobs;
