import axios from 'axios';
import React from 'react';
import {
  Button,
  Form,
  FormControl,
  FormGroup,
  FormLabel,
} from 'react-bootstrap';

const GetAnyJobs = () => {
  const [position, setPosition] = React.useState('');
  const [loading, setLoading] = React.useState(false);
  const [jobs, setJobs] = React.useState(null);

  const setPositionHandler = React.useCallback(
    (e) => setPosition(e.target.value),
    []
  );

  const submitHandler = async (e) => {
    e.preventDefault();
    // alert(position);
    setLoading(true);
    try {
      const queryObject = { function: position };
      //console.log(queryObject);
      const search_jobs = await axios.get(
        `/api/v1/jobs/getjobs/${position}`,
        queryObject
      );
      //alert(search_jobs.data);

      setJobs(search_jobs.data);
      //console.log(jobs);
      setLoading(false);
    } catch (err) {
      console.log(err.message);
      setLoading(false);
    }
  };

  const getusersjobsHandler = async (createdBy) => {
    console.log(createdBy);
  };

  if (loading) {
    return <div className="div-center">LOADING...</div>;
  }

  const Job = (props) => {
    return (
      <div
        className="div-job-container"
        onClick={() => getusersjobsHandler(props.createdBy)}
      >
        <div className="item-container">
          {' '}
          <span>
            <strong> Name: </strong>{' '}
          </span>{' '}
          <span className="right">{props.name} </span>
        </div>
        <div className="item-container">
          {' '}
          <span>
            <strong> Position: </strong>{' '}
          </span>{' '}
          <span className="right">{props.function} </span>
        </div>
        <div className="item-container">
          {' '}
          <span>
            {' '}
            <strong> Company: </strong>{' '}
          </span>{' '}
          <span className="right">{props.company} </span>{' '}
        </div>{' '}
        <div className="item-container">
          {' '}
          <span>
            {' '}
            <strong> Started: </strong>{' '}
          </span>{' '}
          <span className="right">{props.started}</span>{' '}
        </div>
        <div className="item-container">
          {' '}
          <span>
            {' '}
            <strong> Finished: </strong>{' '}
          </span>{' '}
          <span className="right">{props.finished}</span>{' '}
        </div>{' '}
      </div>
    );
  };

  // console.log(jobs);

  let alljobs;
  if (jobs) {
    alljobs = jobs.map((job) => {
      return (
        <Job
          key={job._id}
          createdBy={job.createdBy}
          company={job.company}
          started={job.started}
          finished={job.finished}
          function={job.function}
          name={job.name}
        />
      );
    });
  }

  /* */

  return (
    <div className="div-form-container">
      <Form onSubmit={submitHandler}>
        <FormGroup>
          <FormLabel>Position</FormLabel>
          <FormControl type="text" onChange={(e) => setPositionHandler(e)} />
        </FormGroup>
        <div className="btn-container">
          <Button type="submit">Search</Button>
        </div>
      </Form>
      {alljobs && <div className="job-listing"> {alljobs}</div>}
    </div>
  );
};

export default GetAnyJobs;
