import React from 'react';
import { useGlobalContext } from '../context';

const JobList = () => {
  const { userInfo, findJobs } = useGlobalContext();

  React.useEffect(() => {
    const jobs = findJobs(userInfo._id);
    if (jobs) {
      console.log(jobs);
    }
  }, [findJobs, userInfo._id]);

  return (
    <div className="div-center">
      <h3> {`${userInfo.name}'s Job List`}</h3> <p>{userInfo._id} </p>
    </div>
  );
};

export default JobList;
