import React from 'react';
import { useGlobalContext } from '../context';
import axios from 'axios';

const JobList = () => {
  const { userInfo, setLoading, loading } = useGlobalContext();

  React.useEffect(() => {
    const findJobs = async () => {
      setLoading(true);
      try {
        const jobs = await axios.get(`/api/v1/jobs/getjob/${userInfo._id}`);
        alert('hi');
        console.log(jobs);
        setLoading(false);
      } catch (err) {
        setLoading(false);
        console.log(err.message);
      }
      //alert(jobToFind.position);
    };
    findJobs();
  }, [setLoading, userInfo._id]);

  const Job = (props) => {
    return (
      <div className="div-center">
        <div> {props.function}</div> <div>{props.company} </div>{' '}
        <div>{props.started} </div>
        {props.finished} <div> </div>{' '}
      </div>
    );
  };

  return (
    <div className="div-center">
      <h3> {`${userInfo.name}'s Job List`}</h3> <p>{userInfo._id} </p>
    </div>
  );
};

export default JobList;
