import { useGlobalContext } from '../context';

const Home = () => {
  const { userInfo } = useGlobalContext();
  return (
    <div className="div-center">
      <h3> Personal Details</h3>
      {userInfo && <div> {userInfo.name}</div>} <h4>Save your job here</h4>
    </div>
  );
};

export default Home;
