import {
  Button,
  Form,
  FormControl,
  FormGroup,
  FormLabel,
} from 'react-bootstrap';
import { useGlobalContext } from '../context';
import React from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useCallback } from 'react';

const EditJob = () => {
  const { userInfo } = useGlobalContext();

  const { id } = useParams();

  console.log(id);

  const [position, setPosition] = React.useState('');
  const [company, setCompany] = React.useState('');
  const [started, setStarted] = React.useState('');
  const [finished, setFinished] = React.useState('');

  const [loading, setLoading] = React.useState(false);

  const [job, setJob] = React.useState({});
  const fetchJob = useCallback(async () => {
    try {
      const url = `/api/v1/jobs/getjob/job/${id}`;
      console.log(url);
      const fetched_job = await axios.get(url);
      setJob(fetched_job.data);
      setCompany(job.data.company);
      setPosition(job.data.function);
      setStarted(job.data.started);
      setFinished(job.data.finished);
      //console.log(job);
      setLoading(false);
    } catch (err) {
      console.log(err.message);
      setLoading(false);
    }
  }, [id, job]);

  React.useEffect(() => {
    setLoading(true);

    fetchJob();
  }, [fetchJob, id]);

  console.log(job);

  const submitHandler = (e) => {
    e.preventDefault();
  };

  if (loading) {
    return <div className="div-center">LOADING...</div>;
  }

  return (
    <div className="div-center">
      {userInfo && <div> {userInfo.name}</div>}
      <h3> Edit Job</h3>
      <Form onSubmit={submitHandler}>
        <FormGroup>
          <FormLabel> Position </FormLabel>
          <FormControl
            type="text"
            onChange={(e) => setPosition(e.target.value)}
            value={position}
          />
        </FormGroup>
        <FormGroup>
          <FormLabel> Company </FormLabel>
          <FormControl
            type="text"
            onChange={(e) => setCompany(e.target.value)}
            value={company}
          />
        </FormGroup>
        <FormGroup>
          <FormLabel> Started </FormLabel>
          <FormControl
            type="text"
            onChange={(e) => setStarted(e.target.value)}
            value={started}
          />
        </FormGroup>
        <FormGroup>
          <FormLabel> Finished </FormLabel>
          <FormControl
            type="text"
            onChange={(e) => setFinished(e.target.value)}
            value={finished}
          />
        </FormGroup>
        <div className="btn-container">
          <Button type="submit">Submit</Button>
        </div>
      </Form>
    </div>
  );
};

export default EditJob;
