const reducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN':
      return { ...state, userInfo: action.payload };
    case 'SIGNOUT':
      return { ...state, userInfo: null };
    default:
      return state;
  }
};

export default reducer;
