import axios from 'axios';
import React from 'react';
import {
  Button,
  Form,
  FormControl,
  FormGroup,
  FormLabel,
} from 'react-bootstrap';
import { useGlobalContext } from '../context';

const Details = () => {
  const { userInfo, loading } = useGlobalContext();

  const [position, setPosition] = React.useState('');
  const [company, setCompany] = React.useState('');
  const [begin, setBegin] = React.useState('');
  const [end, setEnd] = React.useState('');

  if (loading) {
    return <div className="div-center"> Loading ... </div>;
  }

  const submitHandler = async (e) => {
    e.preventDefault();

    // alert(JSON.parse(localStorage.getItem('userInfo')));
    try {
      // alert(userInfo._id);
      await axios.post('/api/v1/jobs/postjob', {
        function: position,
        company: company,
        started: begin,
        finished: end,
        createdBy: userInfo._id,
      });
    } catch (err) {
      alert(err.message);
    }
  };

  return (
    <div className="div-center">
      {' '}
      <h3> Personal Details</h3>
      {userInfo && <div> {userInfo.name}</div>}{' '}
      <Form onSubmit={submitHandler}>
        <h4>Save your job here</h4>
        <FormGroup>
          <FormLabel>Position </FormLabel>
          <FormControl
            type="text"
            required
            onChange={(e) => setPosition(e.target.value)}
          />
        </FormGroup>
        <FormGroup>
          <FormLabel>Company </FormLabel>
          <FormControl
            type="text"
            required
            onChange={(e) => setCompany(e.target.value)}
          />
        </FormGroup>
        <FormGroup>
          <FormLabel>Started </FormLabel>
          <FormControl type="text" onChange={(e) => setBegin(e.target.value)} />
        </FormGroup>
        <FormGroup>
          <FormLabel>Finished </FormLabel>
          <FormControl type="text" onChange={(e) => setEnd(e.target.value)} />
        </FormGroup>
        <div className="btn-container">
          <Button type="submit">Submit</Button>
        </div>
      </Form>
    </div>
  );
};

export default Details;
